const express=require("express"),port=8080,bodyParser=require("body-parser"),routes=require("./routes"),app=express(),cors=require("cors");var corsOptions={origin:"http://localhost:8081"};app.use(cors(corsOptions)),app.use(bodyParser.json()),app.use(bodyParser.urlencoded({extended:!0})),routes(app);const server=app.listen(8080,r=>{if(r)return console.log("Error: "+r);console.log("Server listening on port "+server.address().port)});